import React from "react";
import ReactDOM from 'react-dom/client'
import "./index.css"

const pizzaData = [
  {
    name: "Focaccia",
    ingredients: "Bread with italian olive oil and rosemary",
    price: 6,
    photoName: "pizzas/focaccia.jpg",
    soldOut: false,
  },
  {
    name: "Pizza Margherita",
    ingredients: "Tomato and mozarella",
    price: 10,
    photoName: "pizzas/margherita.jpg",
    soldOut: false,
  },
  {
    name: "Pizza Spinaci",
    ingredients: "Tomato, mozarella, spinach, and ricotta cheese",
    price: 12,
    photoName: "pizzas/spinaci.jpg",
    soldOut: false,
  },
  {
    name: "Pizza Funghi",
    ingredients: "Tomato, mozarella, mushrooms, and onion",
    price: 12,
    photoName: "pizzas/funghi.jpg",
    soldOut: false,
  },
  {
    name: "Pizza Salamino",
    ingredients: "Tomato, mozarella, and pepperoni",
    price: 15,
    photoName: "pizzas/salamino.jpg",
    soldOut: true,
  },
  {
    name: "Pizza Prosciutto",
    ingredients: "Tomato, mozarella, ham, aragula, and burrata cheese",
    price: 18,
    photoName: "pizzas/prosciutto.jpg",
    soldOut: false,
  },
];




function App() {

  return <div className="container">
    <Header />
    <Menu />
    <Footer />
  </div>

}

function Menu() {
  const pizzas = pizzaData
  const numPizzas = pizzas.length

  return <main className="menu">
    <h2>Menu</h2>
    {numPizzas > 0 ? (
      <>
        <p>Lorem ipsum dolor sit amet consectetur adipisicing elit. Itaque eum incidunt distinctio dolorum odio pariatur s</p>
        <ul className="pizzas">
          {pizzas.map(pizza => <Pizza pizzaOBJ={pizza} key={pizza.name} />)}

        </ul>
      </>
    ) : <p>We're still working on our menu. Please come back later :)</p>}


    {/*    <Pizza name='Pizza Spinaci' ingredients='Tomato, mozarella, spinach, and ricotta cheese' photoName="pizzas/spinaci.jpg" price={10}></Pizza>
    <Pizza name='Pizza Funghi' ingredients='Tomato, mushrooms' photoName="pizzas/funghi.jpg" price={12}></Pizza>*/}

  </main>
}

function Pizza({ pizzaOBJ }) {

  //if (pizzaOBJ.soldOut) return null


  return <li className={`pizza ${pizzaOBJ.soldOut ? "sold-out" : null}`} >
    <img src={pizzaOBJ.photoName} alt="spinaci pizza" />
    <div>
      <h3>{pizzaOBJ.name}</h3>
      <p>{pizzaOBJ.ingredients}</p>

      {/*{pizzaOBJ.soldOut ? <span>SOLD OUT</span> : <span>{pizzaOBJ.price}</span>}*/}

      <span>{!pizzaOBJ.soldOut ? pizzaOBJ.price : 'SOLD OUT'}</span>
    </div>
  </li>
}

function Header() {
  return <header className="header">
    <h1>Fast React Pizza Co.</h1>
  </header>
}



function Footer() {
  const hour = new Date().getHours();
  const openHour = 12;
  const closeHour = 22;
  const isOpen = hour >= openHour && hour <= closeHour;
  console.log(isOpen)

  //if (!isOpen) {    return <p>Closed</p>  }


  return (<footer className="footer">
    {isOpen ? (
      <Order closeHour={closeHour} openHour={openHour} />
    ) : <p>We're happy to welcome you between {openHour}:00 and {closeHour}:00.</p>}

  </footer>)
}


function Order({ closeHour, openHour }) {

  return (
    <div className="order">
      <p>
        We're open from {openHour}:00 until {closeHour}:00. Come visit us or order online.
      </p>
      <button className="btn">
        Order
      </button>
    </div>
  )
}



//react v18
const root = ReactDOM.createRoot(document.getElementById('root'))
root.render(<React.StrictMode><App></App></React.StrictMode>)
